import { geoNaturalEarth1, geoPath, geoGraticule } from 'd3';

const projection = geoNaturalEarth1()
  .center([10.527004, 52.210297]) // Center over Braunschweig, Germany
  .scale(150000)
  // .translate([450, 300]);

const locations = [
  { name: "Braunschweig Rathaus", coords: [10.5266, 52.2634] },
  { name: "Braunschweig Hbf", coords: [10.5260, 52.2672] },
  { name: "Wolfenb端ttel Hbf", coords: [10.5261, 52.1644] },
  { name: "Wolfenb端ttel Schloss", coords: [10.5265, 52.1629] },
];

export const Markscity = ({
  worldAtlas: { land, interiors },
  data, // Assuming data is still passed for other purposes
  sizeScale,
  sizeValue
}) => (
  <g className="markscity">
    {locations.map((location, index) => {
      const [x, y] = projection(location.coords);
      return (
        <g key={index}> {/* Use index for unique keys */}
          <circle 
            cx={x} 
            cy={y} 
            r={5}
            fill="black" // Ensure fill color is set
          />
          <text
            className="braunschweig"
            textAnchor="middle" // Center the text
            x={x} 
            y={y - 10} // Position text above the circle
          >
            {location.name}
          </text>
        </g>
      );
    })}
  </g>
);
