// BubbleMap/Marks.js
import { geoMercator, geoPath, geoGraticule } from 'd3';

const projection = geoMercator()
  .center([10.5268, 52.2665]) // Center over Braunschweig
  .scale(100000) // Adjust scale as needed
  .translate([450, 300]); // Adjust based on your SVG dimensions

const path = geoPath(projection);
const graticule = geoGraticule();

export const Marks = ({
  worldAtlas: { land, interiors },
  data,
  sizeScale,
  sizeValue
}) => (
  <g className="marks">
    <path className="sphere" d={path({ type: 'Sphere' })} />
    <path className="graticules" d={path(graticule())} />
    {land.features.map((feature, index) => (
      <path key={index} className="land" d={path(feature)} />
    ))}
    <path className="interiors" d={path(interiors)} />
    {data.map((d, index) => {
      const [longitude, latitude] = d.coords; // Ensure coords are in [longitude, latitude]
      const [x, y] = projection([longitude, latitude]); // Project the coordinates
      return (
        <circle key={index} cx={x} cy={y} r={sizeScale(sizeValue(d))} />
      );
    })}
  </g>
);
