import {
    scaleLinear,
    scaleBand,
    max,
    timeFormat,
    extent,
    brushX,
    select
  } from 'd3';
  import { useRef, useEffect } from 'react';
  import { AxisBottom } from './AxisBottom';
  import { AxisLeft } from './AxisLeft';
  import { Marks } from './Marks';

  const margin = { top: 20, right: 30, bottom: 50, left: 90 };

  export const DateHistogram = ({
    data,
    width,
    height,
    setBrushExtent,
    xValue
  }) => {
    const innerHeight = height - margin.top - margin.bottom;
    const innerWidth = width - margin.left - margin.right;

    const xScale = scaleBand()
        .domain(["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"])
        .range([0, innerWidth]);

    const yValue = d => d.HRV; // Use HRV for y values
    const yScale = scaleLinear()
        .domain([-10, max(data, yValue) || 100]) // Ensure max returns a number
        .range([innerHeight, 0])
        .nice();

    const brushRef = useRef();

    useEffect(() => {
        const brush = brushX().extent([[0, 0], [innerWidth, innerHeight]]);
        brush(select(brushRef.current));

        brush.on('brush end', (event) => {
            const selectedDomain = event.selection.map(xScale.invert); // This will be undefined for scaleBand

            // Manually deduce the selected days
            const selectedDays = selectedDomain
                .map(d => xScale.domain().find(day => xScale(day) >= d))
                .filter(Boolean); // Filter out any undefined values

            setBrushExtent(selectedDays); // This will now give you the actual days
        });
    }, [innerWidth, innerHeight, xScale, setBrushExtent]); // Make sure to add setBrushExtent as a dependency

    return (
        <>
            <rect width={width} height={height} fill="white" />
            <g transform={`translate(${margin.left},${margin.top})`}>
                <AxisBottom
                    xScale={xScale}
                    innerHeight={innerHeight+5}
                    tickFormat={d => d} // Format for categorical axis
                />
                <AxisLeft
                    yScale={yScale}
                    innerWidth={innerWidth}
                />
                <Marks
                    data={data}
                    xScale={xScale}
                    yScale={yScale}
                    xValue={xValue} 
                    yValue={yValue} 
                    circleRadius={2.5}
                />
                <g ref={brushRef} />
                <text
                    className="axis-label"
                    textAnchor="middle"
                    transform={`translate(${innerWidth / 2}, ${innerHeight + 35})`} 
                >
                    Days of the Week
                </text>

                <text
                    className="axis-label"
                    textAnchor="middle"
                    transform={`translate(-25, ${innerHeight / 2}) rotate(-90)`} 
                >
                    HRV Score
                </text>
            </g>
        </>
    );
  };
