import React, { useState } from 'react';
import * as d3 from 'd3'; 

export const Marks = ({ bins, data }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    const hrvValues = data.map(d => d.HRV).filter(h => h != null);
    const colorScale = d3.scaleLinear()
        .domain([d3.min(hrvValues), d3.mean(hrvValues), d3.max(hrvValues)]) 
        .range(['blue', 'lightblue', 'darkblue']); 

    return (
        <g className="marks">
            {bins.map((bin, i) => {
                const binData = data.filter(d => 
                    d.x >= bin.x - 15 && d.x <= bin.x + 15 && 
                    d.y >= bin.y - 15 && d.y <= bin.y + 15    
                );

                const averageHRV = binData.length > 0 
                    ? binData.reduce((sum, d) => sum + d.HRV, 0) / binData.length 
                    : 0;

                const fillColor = colorScale(averageHRV);

                return (
                    <g key={i} 
                       onMouseEnter={(e) => {
                           setTooltip({
                               display: 'block',
                               x: e.clientX,
                               y: e.clientY,
                               content: `Count: ${bin.length}, Avg HRV: ${averageHRV.toFixed(2)}`, 
                           });
                       }}
                       onMouseLeave={() => {
                           setTooltip({ display: 'none' });
                       }}>
                    <path
                        d={d3.hexbin().hexagon(15)} // Ensure this matches the radius in Hexbinmap
                        transform={`translate(${bin.x}, ${bin.y})`} 
                        fill={fillColor}
                        stroke="black" 
                        strokeWidth={1}
                        opacity={1} 
                    />
                    </g>
                );
            })}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
