// MapWithHexbins.js
import React from 'react';
import { MapContainer, TileLayer } from 'react-leaflet'; // Import Leaflet components
import { useData } from './useData'; // Import your data hook
import hexbin from 'd3-hexbin'; // Import d3-hexbin
import { geoMercator } from 'd3'; // Import D3 for geo projection

const MapWithHexbins = ({ data }) => {
    const projection = geoMercator()
        .center([10.5268, 52.2665]) // Center for Braunschweig
        .scale(300000) // Zoom level
        .translate([450, 300]); // Translate for your SVG

    // Create hexbin generator
    const hexbinGenerator = hexbin()
        .size([900, 600])
        .radius(20);

    // Generate hexagons
    const hexagons = hexbinGenerator(data.map(d => {
        const [longitude, latitude] = d.coords; // Ensure [longitude, latitude]
        return projection([longitude, latitude]); // Project the coordinates
    }));

    return (
        <MapContainer center={[52.2665, 10.5268]} zoom={12} style={{ height: '600px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            <svg width="900" height="600" style={{ position: 'absolute', top: '0', left: '0' }}>
                {/* D3 Hexagons */}
                {hexagons.map((hex, index) => (
                    <g key={index}>
                        <path
                            d={hex.hexagon()}
                            fill="steelblue"
                            stroke="white"
                            opacity={0.6}
                        />
                    </g>
                ))}
            </svg>
        </MapContainer>
    );
};

export default Marks;
