import React, { useState } from 'react';
import { hexbin as d3Hexbin } from 'd3-hexbin';
import * as d3 from 'd3';

export const Marks = ({ bins }) => {
    const [tooltip, setTooltip] = useState({ display: 'none' });

    return (
        <g className="marks">
            {bins.map((bin, i) => {
                // Set color based on bin count
                let fillColor;
                if (bin.length === 0) {
                    fillColor = 'white'; // No data
                } else if (bin.length > 50) {
                    fillColor = 'darkblue'; // High count
                } else if (bin.length > 20) {
                    fillColor = 'blue'; // Medium count
                } else {
                    fillColor = 'lightblue'; // Low count
                }

                return (
                    <g key={i} 
                       onMouseEnter={(e) => {
                           setTooltip({
                               display: 'block',
                               x: e.clientX,
                               y: e.clientY,
                               content: `Count: ${bin.length}`,
                           });
                       }}
                       onMouseLeave={() => {
                           setTooltip({ display: 'none' });
                       }}>
                        <path
                            d={d3Hexbin().hexagon(30)} // Create hexagon shape
                            transform={`translate(${bin.x}, ${bin.y})`} // Position using centroid
                            fill={fillColor} // Apply color based on the bin length
                            stroke="red" // Stroke color for hexagons
                            strokeWidth={1} // Stroke width
                        />
                    </g>
                );
            })}
            {/* Tooltip */}
            <text 
                x={tooltip.x} 
                y={tooltip.y} 
                style={{ display: tooltip.display, pointerEvents: 'none' }} 
                fill="black" 
                fontSize="12" 
                textAnchor="middle">
                {tooltip.content}
            </text>
        </g>
    );
};
