import React from 'react';
import { useMap } from 'react-leaflet';
import L from 'leaflet'; // For polygon rendering
import { scaleLinear } from 'd3'; // For D3 scaling
import * as d3 from 'd3'; // D3 for additional utilities

export const BubbleMap = ({ data }) => {
    const map = useMap(); // Access Leaflet map instance

    // Create color scale for HRV values
    const colorScale = scaleLinear()
        .domain([d3.min(data, d => d.HRV), d3.mean(data, d => d.HRV), d3.max(data, d => d.HRV)]) 
        .range(['lightblue', 'blue', 'darkblue']); // Color range

    // Ensure there's valid data
    if (!data || data.length === 0) {
        console.error('No data available for visualization');
        return null;
    }

    // Create hexagons for each data point
    data.forEach(point => {
        const coords = point.LocationCoordinates;

        // Validate coordinates before proceeding
        if (!coords || coords.length < 2) {
            console.error("Invalid coordinates for point:", point);
            return; // Skip invalid points
        }

        const hexagon = generateHexagon(coords[1], coords[0], 20); // Generate hexagon vertices

        // Add hexagon to the map
        L.polygon(hexagon, {
            color: colorScale(point.HRV), // Color based on HRV
            fillColor: colorScale(point.HRV),
            fillOpacity: 0.6,
            weight: 1,
        }).addTo(map);
    });

    return null; // No direct rendering required
};

// Function to generate hexagon vertices
const generateHexagon = (lat, lng, radius) => {
    const angle = (Math.PI / 3); // 60 degrees
    const points = [];
    for (let i = 0; i < 6; i++) {
        points.push([
            lat + (radius * Math.sin(angle * i)), // Latitude adjustment
            lng + (radius * Math.cos(angle * i)), // Longitude adjustment
        ]);
    }
    return points; // Return hexagon vertices
};
