import React, { useState } from 'react';

const PageTest = ({ onNext }) => {
    const [isChecked, setIsChecked] = useState(false); // State for checkbox
    const [errorMessage, setErrorMessage] = useState(''); // State for error message

    const handleNext = () => {
        if (!isChecked) {
            setErrorMessage('You must confirm that you can see 4 rectangles in a row before proceeding.'); // Set error message if not checked
        } else {
            setErrorMessage(''); // Clear error message if checked
            onNext(); // Call the onNext function to navigate to the next page
        }
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>This is the Page Test</h1>

            <p className="pagetest">
                <ul>
                    <li>Your screen should display 4 BLACK PARALLEL RECTANGLES in a ROW.</li>
                    <li>If not, adjust your browser's zoom (Windows/Linux: Control +/-)(Mac: Command +/-) to reach the minimum margin on the right and left sides of the screen.</li>
                    <li>ZOOM IN (+) if you see a big gap/space in your screen's left and right margin.</li>
                    <li>ZOOM OUT (-) if you see only 3 rectangles and you do not see the "next" button without scrolling down until you see 4 rectangles in a row.</li>
                    <li>Click Next after adjustment!</li>
                </ul>
            </p>

            {/* Include images as needed */}
            <img src="box.png" alt="Example 1" style={{ width: '450px', margin: '5px' }} />
            <img src="box.png" alt="Example 2" style={{ width: '450px', margin: '5px' }} />
            <img src="box.png" alt="Example 3" style={{ width: '450px', margin: '5px' }} />
            <img src="box.png" alt="Example 4" style={{ width: '450px', margin: '5px' }} />
            {/* Add more images as needed */}
            <br />

            {/* Checkbox for confirmation */}
            <div style={{ margin: '20px' }}>
                <input
                    type="checkbox"
                    checked={isChecked}
                    onChange={() => {
                        setIsChecked(!isChecked);
                        setErrorMessage(''); // Clear error message on change
                    }}
                />
                <label style={{ marginLeft: '5px' }}>
                    I can see 4 rectangles in a row with an optimal size above this text.
                </label>
            </div>

            {/* Display Error Message */}
            {errorMessage && (
                <div style={{ color: 'red', marginTop: '10px' }}>
                    {errorMessage}
                </div>
            )}

            <button
                onClick={handleNext} // Function to navigate to the next page
                style={{ marginTop: '20px', padding: '10px 20px', fontSize: '16px' }}
                className="welcome-button"
            >
                Next
            </button>
        </div>
    );
};

export default PageTest;
