import React, { useRef, useEffect } from 'react';
import { axisLeft } from 'd3';
import * as d3 from 'd3'; // Import d3 if you need functionalities

const AxisLeft = ({ yScale, innerWidth }) => {
    const ref = useRef();

    useEffect(() => {
        const axis = axisLeft(yScale);
        d3.select(ref.current).call(axis);
    }, [yScale]);

    return <g ref={ref} />;
};

export default AxisLeft; // Ensure proper default export
