import React from 'react';
import { scaleSqrt, max } from 'd3';
import { Marks } from './Marks';

const sizeValue = d => d.HRV; // Use HRV for size

export const BubbleMap = ({ data, worldAtlas }) => {
  // No need for size scale in hexbin, but you can keep it if you want for text or colors
  const maxSize = max(data, sizeValue);

  return (
    <Marks
      worldAtlas={worldAtlas}
      data={data}
      sizeValue={sizeValue}
    />
  );
};
