import React, { useEffect } from 'react';
import { useMap } from 'react-leaflet'; // Import Leaflet context
import L from 'leaflet'; // Import Leaflet for map functionalities
import { hexbin } from 'd3-hexbin'; // Import the hexbin functionality
import * as d3 from 'd3'; // Import D3 for utilities

export const BubbleMap = ({ data }) => {
    const map = useMap(); // Get the Leaflet map instance

    useEffect(() => {
        // Create hexbin layer and add it to the map
        const hexLayer = L.hexbinLayer({
            radius: 20, // Adjust radius according to your needs
            opacity: 0.6, // Opacity of the hexagons
            colorRange: ['#f7fbff', '#08306b'], // Color scale
        }).addTo(map);

        // Prepare the data for hexbin layer
        const hexPoints = data.map(feat => {
            const coords = feat.coords; // Ensure `coords` is [lng, lat]
            return [coords[0], coords[1]]; // Hexbin expects [lng, lat]
        });

        // Set data to hexbin layer
        hexLayer.data(hexPoints); 

        // Clean up the layer on unmount
        return () => {
            if (map.hasLayer(hexLayer)) {
                map.removeLayer(hexLayer);
            }
        };
    }, [data, map]); // Depend on data and map to refresh when they change

    return null; // No direct rendering required
};
