import React, { useState } from 'react';

const QGIS = ({ onBack }) => {
    const [isCheckedQGIS, setIsCheckedQGIS] = useState(false); // State to track the first checkbox
    const [isCheckedMap, setIsCheckedMap] = useState(false); // State to track the second checkbox
    const [errorMessage, setErrorMessage] = useState(''); // State for error message

    const handleQGISCheckboxChange = () => {
        setIsCheckedQGIS(!isCheckedQGIS); // Toggle first checkbox state
        setErrorMessage(''); // Clear error message when checkbox changes
    };

    const handleMapCheckboxChange = () => {
        setIsCheckedMap(!isCheckedMap); // Toggle second checkbox state
        setErrorMessage(''); // Clear error message when checkbox changes
    };

    const handleD3Redirect = () => {
        if (!isCheckedQGIS || !isCheckedMap) {
            setErrorMessage('Please confirm that you have finished with the map and submitted the QGIS questionnaire before proceeding.'); // Set error message
        } else {
            // Redirect to the questionnaire URL
            window.open("https://forms.gle/hNiaZBJKq7en76wB7", "_blank");
        }
    };

    return (
        <div style={{ textAlign: 'center', padding: '20px' }}>
            <h1>QGIS Map</h1>
            <p>Here is the data visualization, enjoy your time before deciding to Go to QGIS Questionnaire!</p>
            {/* Display your QGIS images here */}
            <img src="hrv.png" alt="QGIS Example 1" style={{ width: '450px', margin: '5px' }} />
            <img src="stress.png" alt="QGIS Example 2" style={{ width: '450px', margin: '5px' }} />
            <img src="hrv.png" alt="QGIS Example 3" style={{ width: '450px', margin: '5px' }} />
            <img src="stress.png" alt="QGIS Example 4" style={{ width: '450px', margin: '5px' }} />
            {/* Add more images as needed */}
            <br />

            {/* First Checkbox to confirm questionnaire submission */}
            <div style={{ margin: '20px' }}>
                <input
                    type="checkbox"
                    checked={isCheckedQGIS}
                    onChange={handleQGISCheckboxChange}
                />
                <label style={{ marginLeft: '5px' }}>
                    I have finished and submitted the QGIS questionnaire in the Google Form
                </label>
            </div>

            {/* Second Checkbox to confirm readiness to fill the questionnaire */}
            <div style={{ margin: '20px' }}>
                <input
                    type="checkbox"
                    checked={isCheckedMap}
                    onChange={handleMapCheckboxChange}
                />
                <label style={{ marginLeft: '5px' }}>
                    I have finished with the map and ready to fill in the questionnaire
                </label>
            </div>

            {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>} {/* Display error message */}

            <button
                onClick={handleD3Redirect} // Function for redirecting
                className="qgis-que"
                style={{ margin: '5px', padding: '10px 20px' }} // Same styling as above
            >
                Go to QGIS Questionnaire
            </button>

            <button
                onClick={onBack} // Function to go back to D3 React App
                className="qgis-back"
                style={{ margin: '5px', padding: '10px 20px' }} // Same styling as above
            >
                Go to D3-React App
            </button>
        </div>
    );
};

export default QGIS;
