// App.js
import React, { useState } from 'react';
import { useWorldAtlas } from './useWorldAtlas';
import './App.css';
import { useData } from './useData';
import { BubbleMap } from './BubbleMap/index.js';
import { DateHistogram } from './DateHistogram/index.js';

const width = 900;
const height = 750;
const dateHistogramSize = 0.25;
const xValue = d => d.Timestamp; // Correct x-value function

const App = () => {
    const worldAtlas = useWorldAtlas();
    const data = useData(); // Use the JSON data

    const [brushExtent, setBrushExtent] = useState(null);

    // Debug the current data being loaded
    // console.log("Data being loaded:", data);

    if (!worldAtlas || !data || data.length === 0) {
        return <pre>loading</pre>; // Show loading if no data
    }

    const filteredData = brushExtent
        ? data.filter(d => {
            const date = xValue(d);
            return date >= brushExtent[0] && date <= brushExtent[1];
        })
        : data;

    return (

        <svg width={width} height={height}>
            <text
                x={width/2} // Center the title
                y={40} // Position it near the top
                textAnchor="middle" // Center the text
                fontSize="24" // Set font size
                fontWeight="bold" // Make the title bold
                fill="black" // Color of the title
            >
                THE HRV DISTRIBUTION
            </text>

            <g transform={`translate(0, 80)`}>
                <BubbleMap data={filteredData} worldAtlas={worldAtlas} />
            </g>
            <g transform={`translate(0, ${height - dateHistogramSize * height})`}>

                <DateHistogram
                    data={data} // Pass the data for the histogram
                    width={width}
                    height={dateHistogramSize * height}
                    setBrushExtent={setBrushExtent}
                    xValue={xValue} // Pass xValue function
                />

            </g>
        </svg>
    );
};

export default App;
